<template>
  <div class="stars-container">
    <span class="stars-item"
          v-for="(item, index) of starArr"
          :style="{width: size + 'rpx', height: size + 'rpx', 'background-image': 'url(' + item + ')'}"
          :key="index"
          @click="handleSelect(index + 1)"></span>
  </div>
</template>

<script>
export default {
  props: {
    score: {
      type: Number,
      default: 1
    },
    select: {
      type: Boolean,
      default: false
    },
    size: {
      type: Number,
      default: 30
    }
  },
  data() {
    return {
      _score: this.score,
      halfstar:
        "data:image/png;base64,",
      offstar:
        "data:image/png;base64,",
      onstar:
        "data:image/png;base64,"
    };
  },
  methods: {
    handleSelect(score) {
      if (!this.select) return false;
      this._score = score;
      this.$emit("handleSelect", score);
    }
  },
  mounted() {},
  computed: {
    starArr() {
      let starArr = []; //3.9
      //全星星的个数
      let onstar = parseInt(this.score);
      //是否有半星
      let halfStar = Math.round(this.score - onstar) ? true : false;
      //有多少课灰色的星星
      for (let i = 0; i < onstar; i++) {
        starArr.push(this.onstar);
      }
      if (halfStar) {
        starArr.push(this.halfstar);
      }
      while (starArr.length < 5) {
        starArr.push(this.offstar);
      }

      return starArr;
    }
  }
};
</script>

<style lang="scss" scoped>
.stars-container {
  display: flex;
  justify-content: space-around;
  align-items: center;
  width: 100%;
  height: 100%;
  .stars-item {
    background-size: cover;
  }
}
</style>